import { NavLink } from "react-router-dom";
import React, { useState, useEffect } from "react";
import "./Card.css";

function Card() {
  const [DataLog, setData] = useState([]);

  const getData = () => {
    fetch("data.json")
      .then((response) => response.json())
      .then((myJson) => setData(myJson));
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <section className="cards">
      {DataLog.map((data) => (
        <NavLink to={`/data/${data.id}`} key={data.id}>
          <div className="card_div">
            {/* Afficher la bande "VENDU" si le prix est "vendu" */}
            {data.prix.toLowerCase() === "vendu" && (
              <div className="vendu_banner">VENDU</div>
            )}
            <img src={data.cover} alt={data.title} className="card_img" />
            <div className="card_text">
              <p>{data.title}</p>
              {/* N'afficher le prix que s'il n'est pas "vendu" */}
              {data.prix.toLowerCase() !== "vendu" && <p>{data.prix}</p>}
            </div>
          </div>
        </NavLink>
      ))}
    </section>
  );
}

export default Card;
